package com.app.videostream.Model;public class Subject {    String id;    String title;    String description;    String displayImgUrl;    int totalVideos;    public Subject(String id, String title, String description, String displayImgUrl, int totalVideos) {        this.id = id;        this.title = title;        this.description = description;        this.displayImgUrl = displayImgUrl;        this.totalVideos=totalVideos;    }    public Subject(){}    public int getTotalVideos() {        return totalVideos;    }    public void setTotalVideos(int totalVideos) {        this.totalVideos = totalVideos;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public String getDisplayImgUrl() {        return displayImgUrl;    }    public void setDisplayImgUrl(String displayImgUrl) {        this.displayImgUrl = displayImgUrl;    }}